WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{lodging_code}
) SELECT to_char(R.check_in,'yyyy') as reservation_year , count(*) as CNT FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code GROUP BY to_char(R.check_in,'yyyy') ORDER BY 1

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.check_in,'yyyy-mm') as reservation_month , count(*) as CNT FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.reservation_date between #{choice_year}||'-01-01' and #{choice_year}||'-12-31' GROUP BY to_char(R.check_in,'yyyy-mm') ORDER BY 1

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.check_in,'yyyy-mm-dd') as reservation_day , count(*) as CNT FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.reservation_date between extract(year from sysdate)||'-'||#{choice_month}||'-01' and #{choice_month_last_day} GROUP BY to_char(R.check_in,'yyyy-mm-dd') ORDER BY 1

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy') as year, sum(R.reservation_price) as all_profit FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code GROUP BY to_char(R.reservation_date,'yyyy') ORDER BY year

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy-mm') as year, sum(R.reservation_price) as profit_all FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.reservation_date between #{choice_year}||'-01-01' and #{choice_year}||'-12-31' GROUP BY to_char(R.reservation_date,'yyyy-mm') ORDER BY year

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy-mm-dd') as day, sum(R.reservation_price) as profit FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.reservation_date between extract(year from sysdate)||'-'||#{choice_month}||'-01' and #{choice_month_last_day} GROUP BY to_char(R.reservation_date,'yyyy-mm-dd') ORDER BY day


select to_char(count(*)) as count
from tbl_reservation
where status = 1 and fk_room_detail_code = #{room_detail_code} and check_in >= #{check_in} and check_out <= #{check_out}

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy') as year, sum(R.reservation_price) as success_profit FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE status = 1 GROUP BY to_char(R.reservation_date,'yyyy') ORDER BY year

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy') as year, sum(R.reservation_price) as fail_profit FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE status = 2 GROUP BY to_char(R.reservation_date,'yyyy') ORDER BY year

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy-mm') as year, sum(R.reservation_price) as profit_success FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.status = 1 and R.reservation_date between #{choice_year}||'-01-01' and #{choice_year}||'-12-31' GROUP BY to_char(R.reservation_date,'yyyy-mm') ORDER BY year

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy-mm') as year, sum(R.reservation_price) as profit_fail FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.status = 2 and R.reservation_date between #{choice_year}||'-01-01' and #{choice_year}||'-12-31' GROUP BY to_char(R.reservation_date,'yyyy-mm') ORDER BY year

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy-mm-dd') as day, sum(R.reservation_price) as profit_success FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.status = 1 and R.reservation_date between extract(year from sysdate)||'-'||#{choice_month}||'-01' and #{choice_month_last_day} GROUP BY to_char(R.reservation_date,'yyyy-mm-dd') ORDER BY day

WITH A AS ( select R.room_detail_code from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = r.fk_lodging_code
where L.fk_companyid = #{companyid}
) SELECT to_char(R.reservation_date,'yyyy-mm-dd') as day, sum(R.reservation_price) as profit_fail FROM A JOIN tbl_reservation R ON A.room_detail_code = R.fk_room_detail_code WHERE R.status = 2 and R.reservation_date between extract(year from sysdate)||'-'||#{choice_month}||'-01' and #{choice_month_last_day} GROUP BY to_char(R.reservation_date,'yyyy-mm-dd') ORDER BY day

select count(*) as count from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = R.fk_lodging_code JOIN tbl_reservation V ON R.room_detail_code = V.fk_room_detail_code JOIN tbl_member M ON V.fk_userid = M.userid where fk_companyid = #{companyid}
and V.status = #{status}
order by to_number(V.reservation_code) desc

SELECT lodging_name, user_name, room_detail_code, check_in, check_out, room_stock, status, reservation_code, room_name FROM ( SELECT rownum AS RNO ,lodging_name, user_name, room_detail_code, check_in, check_out, room_stock, status, reservation_code, room_name FROM ( select L.lodging_name, M.user_name, R.room_detail_code, to_char(V.check_in,'yyyy-mm-dd') as check_in, to_char(V.check_out,'yyyy-mm-dd') as check_out, R.room_stock, V.status, V.reservation_code, R.room_name from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = R.fk_lodging_code JOIN tbl_reservation V ON R.room_detail_code = V.fk_room_detail_code JOIN tbl_member M ON V.fk_userid = M.userid
where L.fk_companyid = #{companyid}
and V.status = #{status}
order by to_number(V.reservation_code) desc )V ) T where RNO between #{startRno} and #{endRno}

select status
from tbl_reservation
where fk_userid = #{userid}

SELECT lodging_name, user_name, room_detail_code, check_in, check_out, room_stock, status, reservation_code, room_name FROM ( SELECT rownum AS RNO ,lodging_name, user_name, room_detail_code, check_in, check_out, room_stock, status, reservation_code, room_name FROM ( select L.lodging_name, M.user_name, R.room_detail_code, to_char(V.check_in,'yyyy-mm-dd') as check_in, to_char(V.check_out,'yyyy-mm-dd') as check_out, R.room_stock, V.status, V.reservation_code, R.room_name from tbl_lodging L JOIN tbl_room_detail R on L.lodging_code = R.fk_lodging_code JOIN tbl_reservation V ON R.room_detail_code = V.fk_room_detail_code JOIN tbl_member M ON V.fk_userid = M.userid
where V.fk_userid = #{userid}
and V.status = #{status}
order by V.reservation_date desc )V ) T where RNO between #{startRno} and #{endRno}

select email
from tbl_member
where email = #{email} and userid != #{userid}

select scheduleno , startdate, enddate , smcatgoname, lgcatgoname, user_name , subject, content from ( select row_number() over(order by SD.scheduleno desc) as rno , SD.scheduleno , to_char(SD.startdate, 'yyyy-mm-dd hh24:mi') as startdate , to_char(SD.enddate, 'yyyy-mm-dd hh24:mi') as enddate , SC.smcatgoname, LC.lgcatgoname, M.user_name , SD.subject, SD.content from tbl_calendar_schedule SD JOIN tbl_member M ON SD.fk_userid = M.userid JOIN tbl_calendar_small_category SC ON SD.fk_smcatgono = SC.smcatgono JOIN tbl_calendar_large_category LC ON SD.fk_lgcatgono = LC.lgcatgono
where ( to_char(SD.startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) AND ( to_char(SD.enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} )
and ( SD.fk_lgcatgono = 2 OR SD.fk_userid = #{fk_userid} OR ( SD.fk_userid != #{fk_userid} and lower(SD.joinuser) like '%'||lower(#{fk_userid})||'%' ) )
and ( SD.fk_lgcatgono = 1 AND SD.fk_userid = #{fk_userid} )
and SD.fk_lgcatgono = 1 and SD.fk_userid = #{fk_userid} and lower(SD.joinuser) like '%'||lower(#{searchWord})||'%'
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
and SD.fk_lgcatgono = 1 and SD.fk_userid = #{fk_userid}
and SD.fk_lgcatgono = 2
) V where V.rno between #{startRno} and #{endRno}

mapper.update:
update tbl_calendar_schedule set startdate = to_date(#{startdate}, 'yyyymmddhh24miss')
                               , enddate = to_date(#{enddate}, 'yyyymmddhh24miss')
                               , subject = #{subject}
                               , fk_lgcatgono = 1
                               , fk_smcatgono = #{fk_smcatgono}
                               , color = #{color}
                               , place = #{place}
                               , joinuser = #{joinuser}
                               , content = #{content}
where scheduleno = #{scheduleno}
