mapper.select:
SELECT rno, food_store_code, local_status, food_name, food_content, food_businesshours, food_mobile , food_address, food_main_img, review_division, food_category , filename, orgfilename, filesize , readcount FROM ( SELECT rownum as RNO, food_store_code, local_status, food_name, food_content, food_businesshours, food_mobile , food_address, food_main_img, review_division, food_category , filename, orgfilename, filesize , readcount FROM ( select food_store_code, local_status, food_name, food_content, food_businesshours, food_mobile , substr(food_address, 0, instr(food_address, ' ', 1, 2)-1) AS food_address , food_main_img, review_division, food_category , filename, orgfilename, filesize , readcount from tbl_food_store where 1=1
AND food_category IN
'${arr_category[i]}'
AND local_status IN
'${arr_area[i]}'
AND lower(food_name) like '%'||lower(#{searchWordFood})||'%'
ORDER BY food_store_code desc
ORDER BY ${orderType} desc
ORDER BY ${orderType} ${orderValue_asc}
ORDER BY ${orderType} ${orderValue_desc}
)V )V2 WHERE rno between #{startRno} and #{endRno}

select food_store_code, local_status, food_name, food_category, food_content, food_address, food_businesshours, food_mobile
	 , food_main_img, review_division, readcount
from tbl_food_store


	where food_store_code = #{food_store_code}



	where food_store_code = #{random_recommend_code}

SELECT I.food_add_img FROM ( select food_store_code, food_name from tbl_food_store
where food_store_code = #{food_store_code}
where food_store_code = #{random_recommend_code}
)S JOIN tbl_food_add_img I ON S.food_store_code = I.fk_food_store_code ORDER BY food_add_img


	select lodging_code, local_status, main_img, lodging_name
	from (
	    select lodging_code, local_status, main_img, lodging_name
	    from tbl_lodging
	    where local_status = #{local_status}
	    order by DBMS_RANDOM.RANDOM
	)
	where rownum <= 2

select count(*)
from tbl_review
where parent_code = #{parent_code}

select count(*)
from tbl_like
where parent_code = #{parent_code}

select fk_userid,parent_code,like_division_R
from tbl_like
