select faq_seq, faq_category, faq_question, faq_answer from ( select rownum as rno, faq_seq, faq_category, faq_question, faq_answer from ( select faq_seq, faq_category, faq_question, faq_answer from tbl_faq where 1=1
and faq_category = #{faq_category}
and faq_question like '%'||lower(#{searchWordFaq})||'%'
order by cast(faq_seq AS int) desc )V )V2 where RNO between #{startRno} and #{endRno}

select count(*) from tbl_faq where 1=1
and faq_category = #{faq_category}
and faq_question like '%'||lower(#{searchWordFaq})||'%'
